settings.liquid:
{% if product.metafields.hpz.badge_rank.value != blank %}
<div class="product-info__badge-list">
<span class="best-rank">#{{ product.metafields.hpz.badge_rank.value }} BEST SELLER
</span>
<p style="color: #777;font-size: 0.9rem;display:flex;align-items: center;">in</p>
<a href="{{ product.metafields.hpz.badge_rank_collection_link.value }}" style="text-decoration:underline;color:#777;display:flex;align-items: center;"><p style="font-size: 0.9rem;color:#777777;display:flex;align-items: center;">{{ product.metafields.hpz.badge_rank_collection_title.value }}</p></a>
</div>
{% endif %}
<style>
.best-rank{
display: inline-block;
    box-sizing: border-box;
    color: #444;
    background-color: #FFDF97;
    padding: 6px 10px;
    font-size:0.7rem;
    position: relative;
    height: 25px;
    border-radius: 90px 90px 90px 90px;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 1px;
display:flex;
align-items: center;
}
.product-info__badge-list {
gap: var(--spacing-2);
    flex-wrap: wrap;
    display: flex;
}
.product-info__liquid {
margin-block-start: 0.5rem;
    margin-block-end: 0.5rem;
}</style>
