settings.content:
{{ product.metafields.hpz.product_tab_3_content.value }}

{% assign other-details = product.metafields.hpz.other_details_json.value %}
{% assign manual-details = product.metafields.hpz.manuals_and_guides_json.value %}

 <p class="other-details-drop">OTHER DETAILS</p>
    <hr>
<div class="table-content-wrapper grid">
    {% if other-details.generic != blank %}
    <div class="table-content">
        <table width="100%">
            <tbody>
                {% if other-details.generic != blank %}
                <tr>
                    <td>GENERIC NAME</td>
                    <td>{{ other-details.generic }}</td>
                </tr>
                {% endif %}


                {% if other-details.country-origin != blank %}
                <tr>
                    <td>COUNTRY OF ORIGIN</td>
                    <td>{{ other-details.country-origin }}</td>
                </tr>
                {% endif %}

                {% if other-details.importer-details != blank %}
                <tr>
                    <td>IMPORTER DETAILS</td>
                    <td>{{ other-details.importer-details }}</td>
                </tr>
                {% endif %}

                {% if other-details.dimensions != blank %}
                <tr>
                    <td>DIMENSIONS</td>
                    <td>{{ other-details.dimensions }}</td>
                </tr>
                {% endif %}

                {% if other-details.qty != blank %}
                <tr>
                    <td>NET QUANTITY</td>
                    <td>{{ other-details.qty }}</td>
                </tr>
                {% endif %}

            </tbody>
        </table>
    </div>

    <div class="table-content">
        <table width="100%">
            <tbody>
                {% if other-details.manufacturer != blank %}
                <tr>
                    <td>MANUFACTURER DETAILS</td>
                    <td>{{ other-details.manufacturer }}</td>
                </tr>
                {% endif %}


                {% if other-details.box != blank %}
                <tr>
                    <td>PACKAGE CONTENTS</td>
                    <td>{{ other-details.box }}</td>
                </tr>
                {% endif %}
            </tbody>
        </table>
    </div>
    {% endif %}
</div>


{% if manual-details.title-1 != blank %}

<div class="table-content-wrapper grid second-row">
    <div class="table-content">
    <strong>MANUALS & GUIDES</strong>
    <table width="100%">
        <tbody>
            <tr>
                <td>
                    <img src="{{ manual-details.img-1 }}" style="width: 30px;display:inline;padding-right: 10px;">
                    <span class="manual-title"><a href="{{ manual-details.link-1 }}" target="_blank">{{
                            manual-details.title-1 }}</a></span>
                    <p class="manual-subtitle">{{ manual-details.subtitle-1 }}</p>

                    <img src="{{ manual-details.img-2 }}" style="width: 30px;display:inline;padding-right: 10px;">
                    <span class="manual-title"><a href="{{ manual-details.link-2 }}" target="_blank">{{
                            manual-details.title-2 }}</a></span>
                    <p class="manual-subtitle"> {{ manual-details.subtitle-2 }}</p>

                    <img src="{{ manual-details.img-3 }}" style="width: 30px;display:inline;padding-right: 10px;">
                    <span class="manual-title"><a href="{{ manual-details.link-3 }}" target="_blank">{{
                            manual-details.title-3 }}</a></span>
                    <p class="manual-subtitle"> {{ manual-details.subtitle-3 }}</p>


                    <img src="{{ manual-details.img-4 }}" style="width: 30px;display:inline;padding-right: 10px;">
                    <span class="manual-title"><a href="{{ manual-details.link-4 }}" target="_blank">{{
                            manual-details.title-4 }}</a></span>
                    <p class="manual-subtitle"> {{ manual-details.subtitle-4 }}</p>

                </td>

            </tr>

        </tbody>
    </table>
    </div>
</div>
{% endif %}




<style>
    .manual-title {
        color: #444;
        font-family: var(--text-font-family);
        font-weight: 500;
        font-size: 16px;
        text-align: left !important;
        display: block;
        letter-spacing: 0.5px;
    }

    .manual-subtitle {
        margin-top: 0px;
        color: #848484;
        text-transform: capitalize;
        font-weight: 400;
        font-size: 14px;
        letter-spacing: 0.9px;
        text-align: left;
        padding-top: 5px;
        padding-bottom: 10px;
        padding-left: 35px;
        margin-top: 0px !important;
    }
.other-details-drop {
font-size: 14px;
text-align: left!important;
letter-spacing: 2px;
font-weight: bolder;
padding: 30px 0 15px 0;
}
</style>

