settings.liquid:
{{ product.metafields.hpz.experience_details }}
<style>
.connect-venue-grid{
  display: grid;
  grid-template-columns: 1fr 4fr;
}

.experience-details-grid {
  display: grid;
  grid-template-columns: 10% 60%;
  background-color: #f7f7f7;
  grid-gap: 0.4rem;
  padding-left: 20px;
  padding-bottom: 10px;
  padding-top: 10px;
background-color:#f9f9f9;
border-radius:1rem;

}    

.experience-details-grid div {
 align-self: center;
}

 @media screen and (max-width: 740px) {
  .experience-details-grid {
    display: grid;
    grid-template-columns: 20% 80%;
  }
}


.experience-details-grid span { align-self:center;
    font-size: 1rem;
    font-weight: 700;
    letter-spacing: 1.1;
    font-family: 'Roboto',sans-serif;
    font-weight: var(--heading-font-weight);
    font-style: var(--heading-font-style);
    letter-spacing: var(--heading-letter-spacing);
    text-transform: capitalize;
    overflow-wrap: anywhere;
    line-height: 1.1;


}

.experience-details-grid p, .connect-venue-grid span {  
  font-size: 14px;
  font-weight: 600;
text-transform: uppercase;
letter-spacing: 0.1rem;

}

.experience-details-grid a {
text-decoration: underline;
}

.experience-details-grid img {  
    width: 30px;   
}    
</style>

{% if product.first_available_variant.inventory_quantity > 0 and product.first_available_variant.inventory_quantity < 51 %}
          <div class="items-left">Hurry up only {{ product.first_available_variant.inventory_quantity }} passes left in stock!</div>
        {% endif %}
