  .manual-subtitle { margin-top: 0px; color: #848484; text-transform: capitalize; font-weight: 400; font-size: 14px; letter-spacing: 0.9px; text-align: left; padding-bottom: 10px; padding-left: 35px; margin-top: 0px !important;}

.manual-title a {
